import React, { useState } from "react";

function Fibonacci() {
  const [numero, setNumero] = useState("");
  const [resultado, setResultado] = useState("");

  const calcularFibonacci = (n) => {
    if (n === 0) return 0;
    if (n === 1) return 1;
    return calcularFibonacci(n - 1) + calcularFibonacci(n - 2);
  };

  const handleInputChange = (event) => {
    setNumero(event.target.value);
  };

  const handleCalcularClick = () => {
    const fib = calcularFibonacci(numero);
    setResultado(`O termo ${numero} da sequência de Fibonacci é ${fib}.`);
  };

  return (
    <div>
      <label>Digite o termo desejado:</label>
      <input type="number" value={numero} onChange={handleInputChange} />
      <button onClick={handleCalcularClick}>Calcular</button>
      {resultado && <div>{resultado}</div>}
    </div>
  );
}

export default Fibonacci;
