import React, { useState } from 'react';

function Nota() {
  const [n, setN] = useState(0);
  const [notas, setNotas] = useState([]);
  const [resultado, setResultado] = useState('');

  const handleNChange = (event) => {
    setN(event.target.value);
    setNotas([]);
  };

  const handleNotaChange = (event, index) => {
    const newNotas = [...notas];
    newNotas[index] = event.target.value;
    setNotas(newNotas);
  };

  const handleSubmit = () => {
    let reprovados = 0;
    let aprovados = 0;

    for (let i = 0; i < n; i++) {
      if (notas[i] < 5) {
        reprovados++;
      } else {
        aprovados++;
      }
    }

    setResultado(`Foram aprovados ${aprovados} e ${reprovados} foram reprovados.`);
  };

  const renderNotas = () => {
    const notasInputs = [];

    for (let i = 0; i < n; i++) {
      notasInputs.push(
        <div key={i}>
          <label>
            Nota {i + 1}:
            <input type="number" value={notas[i] || ''} onChange={(e) => handleNotaChange(e, i)} />
          </label>
        </div>
      );
    }

    return notasInputs;
  };

  return (
    <div>
      <div>
        <label>
          Quantas notas vocÃª quer analisar?
          <input type="number" value={n} onChange={handleNChange} />
        </label>
      </div>
      {n > 0 && (
        <div>
          {renderNotas()}
          <button onClick={handleSubmit}>Calcular notas</button>
        </div>
      )}
      {resultado && <div>{resultado}</div>}
    </div>
  );
}

export default Nota;
