import React, { useState } from "react";

function Somatorio() {
  const [qtdeNumeros, setQtdeNumeros] = useState("");
  const [somatorio, setSomatorio] = useState(0);

  const handleQtdeNumerosChange = (event) => {
    setQtdeNumeros(event.target.value);
  };

  const handleCalcularClick = () => {
    let somatorioAtual = somatorio;
    for (let i = 0; i < Number(qtdeNumeros); i++) {
      const novoNumero = prompt(`Digite o ${i + 1}º número:`);
      somatorioAtual += Number(novoNumero);
    }
    setSomatorio(somatorioAtual);
  };

  return (
    <div>
      <label>Quantidade de números a serem somados:</label>
      <input type="number" value={qtdeNumeros} onChange={handleQtdeNumerosChange} />
      <button onClick={handleCalcularClick}>Calcular</button>
      <div>O somatório é: {somatorio}</div>
    </div>
  );
}

export default Somatorio;
