import { useState } from "react";

function Fibonacci (){
    const [numero, setNumero] = useState("")
    const [resultado, setResultado] = useState("")

    const calcularFibonacci = (n) => {
     if (n === 0) return 0;
    if (n === 1) return 1;
    return calcularFibonacci(n - 1) + calcularFibonacci(n - 2);
    }

    const aoAlterarNumero=(evento)=>{
        setNumero(evento.target.value)
    }
    const aoClicarParaCalcular = () =>{
       const num =  calcularFibonacci(numero);
       setResultado(`O termo ${numero} da sequência de Fibonacci é ${num}.`);
    }

    return (
        <div>
         <label>digite o termo desejado: </label>
        <input type ="number" value ={numero} onChange={aoAlterarNumero}/>
        <button onClick={aoClicarParaCalcular}/>
        {resultado && <div>resultado</div>} 

        </div>

    )


}
export default Fibonacci;
